package win_calculator.model.utils;

import win_calculator.model.exceptions.MyException;

import java.math.BigDecimal;
import java.math.RoundingMode;

public abstract class ModelUtils {


    private static final String ZERO = "0";
    private static final int ROUND_MODULE = 4;
    private static final int MAX_EXPONENT = 9999;
    private static final String E = "E";
    private static final String OVERFLOW_MSG = "Overflow";
    private static final BigDecimal ROUND_VALUE = new BigDecimal("0.");

    private static boolean isOverflow(BigDecimal number) {

        boolean result = false;
        String strNumber = number.toString();
        if (strNumber.contains(E)) {
            String[] numberParts = strNumber.split(E);
            if (Math.abs(Integer.parseInt(numberParts[1])) > MAX_EXPONENT && !numberParts[0].equals(ZERO)) {
                result = true;
            }
        } else {
            if (number.toBigInteger().toString().length() > MAX_EXPONENT + 1) {
                result = true;
            }
        }
        return result;
    }

    public static BigDecimal roundNumber(BigDecimal number){

        BigDecimal result = number;
        String str = number.toString();
        int length = str.length();
        if (length > MAX_EXPONENT){
            if (Integer.parseInt(str.charAt(length - 1)+"") > ROUND_MODULE){
                result = result.add(ROUND_VALUE);
            }
        }else {
            result = number.setScale(10000,RoundingMode.HALF_UP);
        }
        return result;
    }

    public static void checkOnOverflow(BigDecimal number) throws MyException {

        if (isOverflow(number)){
            throw new MyException(OVERFLOW_MSG);
        }
    }

}
